INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2A-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.4, 'If one zero of the polynomial p(x) = 5x² + 13x + k is the reciprocal of the other, then the value of k is:', NULL, 0, NULL, '[{"id":"A","text":"1/5","is_correct":false},{"id":"B","text":"5","is_correct":true},{"id":"C","text":"13","is_correct":false},{"id":"D","text":"0","is_correct":false}]', '{"steps":["Let zeros be α and 1/α","Product of zeros = c/a","α × (1/α) = k/5","1 = k/5","k = 5"],"answer":{"correct_option":"B"}}', NULL, NULL, NULL, 1);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U3-B3A-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.3, 'The distance of the point P(-3, -4) from the origin is:', NULL, 0, NULL, '[{"id":"A","text":"7","is_correct":false},{"id":"B","text":"1","is_correct":false},{"id":"C","text":"5","is_correct":true},{"id":"D","text":"-5","is_correct":false}]', '{"steps":["Distance from origin = √(x² + y²)","√((-3)² + (-4)²) = √(9 + 16)","√25 = 5 (Distance is always positive)"],"answer":{"correct_option":"C"}}', NULL, NULL, NULL, 2);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U5-B5A-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.4, 'If sin A + sin² A = 1, then the value of cos² A + cos⁴ A is:', 'If $\sin A + \sin^2 A = 1$, then the value of $\cos^2 A + \cos^4 A$ is:', 0, NULL, '[{"id":"A","text":"1","is_correct":true},{"id":"B","text":"1/2","is_correct":false},{"id":"C","text":"2","is_correct":false},{"id":"D","text":"3","is_correct":false}]', '{"steps":["Given sin A = 1 - sin² A","sin A = cos² A","Squaring both sides: sin² A = cos⁴ A","We need value of cos² A + cos⁴ A","Substitute cos⁴ A = sin² A","Expression becomes cos² A + sin² A","Identity: cos² A + sin² A = 1"],"answer":{"correct_option":"A"}}', NULL, NULL, NULL, 3);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U4-B4A-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.4, 'In ΔABC, DE || BC such that AD = 3 cm, DB = 5 cm and AE = 6 cm. The length of AC is:', NULL, 1, '<svg viewBox=''0 0 200 150'' xmlns=''http://www.w3.org/2000/svg''><polygon points=''100,10 10,140 190,140'' stroke=''black'' fill=''none'' stroke-width=''2''/><line x1=''55'' y1=''75'' x2=''145'' y2=''75'' stroke=''black'' stroke-width=''2''/><text x=''100'' y=''5'' font-size=''12'' text-anchor=''middle''>A</text><text x=''5'' y=''145'' font-size=''12'' text-anchor=''middle''>B</text><text x=''195'' y=''145'' font-size=''12'' text-anchor=''middle''>C</text><text x=''50'' y=''75'' font-size=''12'' text-anchor=''end''>D</text><text x=''150'' y=''75'' font-size=''12'' text-anchor=''start''>E</text></svg>', '[{"id":"A","text":"10 cm","is_correct":false},{"id":"B","text":"16 cm","is_correct":true},{"id":"C","text":"8 cm","is_correct":false},{"id":"D","text":"12 cm","is_correct":false}]', '{"steps":["By BPT: AD/DB = AE/EC","3/5 = 6/EC","3 × EC = 30 => EC = 10 cm","AC = AE + EC = 6 + 10 = 16 cm"],"answer":{"correct_option":"B"}}', NULL, NULL, NULL, 4);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2B-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.4, 'The value of k for which the system of equations kx + 3y = 1, 12x + ky = 2 has no solution is:', 'The value of $k$ for which the system of equations $kx + 3y = 1$, $12x + ky = 2$ has no solution is:', 0, NULL, '[{"id":"A","text":"k = ±6","is_correct":false},{"id":"B","text":"k = 6","is_correct":false},{"id":"C","text":"k = -6","is_correct":true},{"id":"D","text":"k = 0","is_correct":false}]', '{"steps":["For no solution: a1/a2 = b1/b2 ≠ c1/c2","k/12 = 3/k","k² = 36","k = ±6","Check c1/c2 = 1/2. If k=6, 6/12 = 3/6 = 1/2. Wait, if equal to c1/c2 it is infinite solutions.","Let''s recheck question text. Usually c varies.","Assume infinite? No, question says No solution.","If k=-6: -6/12 = 3/-6 = -1/2. ≠ 1/2. Correct.","So for No Solution, k should be -6.","Correct option set to -6 in theory, but let''s re-verify options. Answer key says k=6 usually implies parallel lines if c is different.","Here c1/c2 is 1/2.","If k=6 => ratio is 1/2 = 1/2 = 1/2 (Dependent/Infinite).","If k=-6 => ratio is -1/2 = -1/2 ≠ 1/2 (Parallel/No Solution).","Correction: Correct option should be k = -6."],"steps_latex":["For no solution: $\\frac{a_1}{a_2} = \\frac{b_1}{b_2} \\neq \\frac{c_1}{c_2}$","$\\frac{k}{12} = \\frac{3}{k}$","$k^2 = 36 \\Rightarrow k = \\pm 6$","Check $\\frac{c_1}{c_2} = \\frac{1}{2}$","Case 1: If $k=6$, $\\frac{6}{12} = \\frac{1}{2} = \\frac{1}{2}$ (Infinite solutions)","Case 2: If $k=-6$, $\\frac{-6}{12} = -\\frac{1}{2} \\neq \\frac{1}{2}$ (No solution)","Therefore, for no solution, $k = -6$."],"answer":{"correct_option":"C","note":"Adjusted to C based on logic"}}', NULL, NULL, NULL, 5);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2D-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.3, 'The 11th term of the AP: -5, -5/2, 0, 5/2, ... is:', 'The 11th term of the AP: $-5, -\frac{5}{2}, 0, \frac{5}{2}, \dots$ is:', 0, NULL, '[{"id":"A","text":"-20","is_correct":false},{"id":"B","text":"20","is_correct":true},{"id":"C","text":"-30","is_correct":false},{"id":"D","text":"30","is_correct":false}]', '{"steps":["a = -5, d = -5/2 - (-5) = 2.5 (5/2)","a11 = a + 10d","a11 = -5 + 10(2.5)","a11 = -5 + 25 = 20"],"answer":{"correct_option":"B"}}', NULL, NULL, NULL, 6);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2C-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.3, 'Which of the following is a quadratic equation?', 'Which of the following is a quadratic equation?', 0, NULL, '[{"id":"A","text":"x² + 3x + 1 = (x-2)²","is_correct":false},{"id":"B","text":"(x+1)² = 2(x-3)","is_correct":true},{"id":"C","text":"x² + 1/x = 5","is_correct":false},{"id":"D","text":"2x² - 5x = (x-1)² + x²","is_correct":false}]', '{"steps":["A: x² + 3x + 1 = x² - 4x + 4 => 7x - 3 = 0 (Linear)","B: x² + 2x + 1 = 2x - 6 => x² + 7 = 0 (Quadratic)","C: Multiply by x => x³ (Cubic)","D: 2x² = 2x² cancels out (Linear)"],"steps_latex":["Option A: $x^2 + 3x + 1 = x^2 - 4x + 4 \\Rightarrow 7x - 3 = 0$ (Linear)","Option B: $x^2 + 2x + 1 = 2x - 6 \\Rightarrow x^2 + 7 = 0$ (Quadratic)","Option C: Multiply by $x \\Rightarrow x^3$ (Cubic)","Option D: $2x^2$ on both sides cancels out (Linear)"],"answer":{"correct_option":"B"}}', NULL, NULL, NULL, 7);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U4-B4B-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.3, 'From a point Q, the length of the tangent to a circle is 24 cm and the distance of Q from the centre is 25 cm. The radius of the circle is:', NULL, 0, NULL, '[{"id":"A","text":"7 cm","is_correct":true},{"id":"B","text":"12 cm","is_correct":false},{"id":"C","text":"15 cm","is_correct":false},{"id":"D","text":"24.5 cm","is_correct":false}]', '{"steps":["Tangent is perpendicular to radius.","Hypotenuse (OQ) = 25, Tangent (PQ) = 24","r² + 24² = 25²","r² = 625 - 576 = 49","r = 7 cm"],"answer":{"correct_option":"A"}}', NULL, NULL, NULL, 8);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U4-Filler-Q41', 'cbse-10-math-full-01', 'MCQ', 1, 0.5, 'In ΔABC, if AB=6√3 cm, AC=12 cm and BC=6 cm, then angle B is:', NULL, 0, NULL, '[{"id":"A","text":"120°","is_correct":false},{"id":"B","text":"60°","is_correct":false},{"id":"C","text":"90°","is_correct":true},{"id":"D","text":"45°","is_correct":false}]', '{"steps":["Check Pythagoras theorem.","AB² = 108, BC² = 36, AC² = 144.","108 + 36 = 144 => AB² + BC² = AC².","Therefore ∠B (opposite AC) = 90°."],"answer":{"correct_option":"C"}}', 'Geometry', NULL, NULL, 9);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U1-B1A-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.3, 'The ratio of LCM and HCF of the least composite and the least prime numbers is:', 'The ratio of LCM and HCF of the least composite and the least prime numbers is:', 0, NULL, '[{"id":"A","text":"1:2","is_correct":false},{"id":"B","text":"2:1","is_correct":true},{"id":"C","text":"1:1","is_correct":false},{"id":"D","text":"1:3","is_correct":false}]', '{"steps":["Least composite number = 4","Least prime number = 2","LCM(4, 2) = 4","HCF(4, 2) = 2","Ratio LCM:HCF = 4:2 = 2:1"],"steps_latex":["Least composite number = $4$","Least prime number = $2$","LCM$(4, 2) = 4$","HCF$(4, 2) = 2$","Ratio LCM:HCF = $4:2 = 2:1$"],"answer":{"correct_option":"B"}}', NULL, NULL, NULL, 10);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U1-B1A-Q02', 'cbse-10-math-full-01', 'MCQ', 1, 0.4, 'If HCF(336, 54) = 6, find LCM(336, 54).', 'If HCF$(336, 54) = 6$, find LCM$(336, 54)$.', 0, NULL, '[{"id":"A","text":"3024","is_correct":true},{"id":"B","text":"3360","is_correct":false},{"id":"C","text":"3004","is_correct":false},{"id":"D","text":"5400","is_correct":false}]', '{"steps":["Formula: HCF × LCM = Product of numbers","6 × LCM = 336 × 54","LCM = (336 × 54) / 6","LCM = 336 × 9 = 3024"],"steps_latex":["Formula: HCF $\\times$ LCM = Product of numbers","$6 \\times \\text{LCM} = 336 \\times 54$","$\\text{LCM} = \\frac{336 \\times 54}{6}$","$\\text{LCM} = 336 \\times 9 = 3024$"],"answer":{"correct_option":"A"}}', NULL, NULL, NULL, 11);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U1-B1B-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.4, 'The decimal expansion of the rational number 14587/1250 will terminate after:', NULL, 0, NULL, '[{"id":"A","text":"one decimal place","is_correct":false},{"id":"B","text":"two decimal places","is_correct":false},{"id":"C","text":"three decimal places","is_correct":false},{"id":"D","text":"four decimal places","is_correct":true}]', '{"steps":["Denominator 1250 = 2 × 5^4","Highest power is 4 (from 5^4)","Therefore, it terminates after 4 decimal places."],"answer":{"correct_option":"D"}}', NULL, NULL, NULL, 12);
