INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U7-B7B-Q01', 'cbse-10-math-full-01', 'MCQ', 1, 0.3, 'A card is selected at random from a well shuffled deck of 52 cards. The probability of its being a red face card is:', NULL, 0, NULL, '[{"id":"A","text":"3/26","is_correct":false},{"id":"B","text":"3/13","is_correct":false},{"id":"C","text":"2/13","is_correct":false},{"id":"D","text":"1/2","is_correct":false}]', '{"steps":["Total cards = 52.","Face cards (J, Q, K) = 12 total.","Red face cards (Hearts + Diamonds) = 6.","Probability = 6/52 = 3/26"],"answer":{"correct_option":"A"}}', NULL, NULL, NULL, 14);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U3-B3A-Q02', 'cbse-10-math-full-01', 'VSA', 2, 0.5, 'Find the ratio in which the y-axis divides the line segment joining the points A(5, -6) and B(-1, -4).', NULL, 0, NULL, NULL, '{"steps":["Let ratio be k:1. Point on y-axis is (0, y).","Using Section Formula for x-coordinate: x = (mx₂ + nx₁)/(m+n)","0 = (k(-1) + 1(5))/(k+1)","0 = -k + 5","k = 5","Ratio is 5:1"],"answer":"5:1"}', NULL, NULL, NULL, 15);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U6-Filler-Q40', 'cbse-10-math-full-01', 'VSA', 2, 0.5, 'Two cubes each of volume 64 cm³ are joined end to end. Find the surface area of the resulting cuboid.', NULL, 0, NULL, NULL, '{"steps":["Volume a³ = 64 => a = 4 cm.","Length of cuboid l = 4+4 = 8 cm.","Breadth b = 4 cm. Height h = 4 cm.","TSA = 2(lb + bh + hl)","= 2(32 + 16 + 32)","= 2(80) = 160 cm²"],"answer":"160 cm²"}', 'Mensuration', NULL, NULL, 16);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U1-B1A-Q03', 'cbse-10-math-full-01', 'VSA', 2, 0.5, 'Explain why 7 × 11 × 13 + 13 is a composite number.', 'Explain why $7 \times 11 \times 13 + 13$ is a composite number.', 0, NULL, NULL, '{"steps":["Take 13 common: 13(7 × 11 + 1)","= 13(77 + 1)","= 13 × 78","Since the number has factors other than 1 and itself (13 and 78), it is a composite number."],"steps_latex":["Take $13$ common: $13(7 \\times 11 + 1)$","$= 13(77 + 1)$","$= 13 \\times 78$","Since the number has factors other than $1$ and itself ($13$ and $78$), it is a composite number."],"answer":"Composite because it has factors 13 and 78."}', NULL, NULL, NULL, 17);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U7-B7B-Q02', 'cbse-10-math-full-01', 'VSA', 2, 0.4, 'Two dice are thrown at the same time. Find the probability of getting different numbers on the dice.', NULL, 0, NULL, NULL, '{"steps":["Total outcomes = 6 × 6 = 36.","Favorable outcomes (Same number/Doublets): (1,1), (2,2)...(6,6) = 6 outcomes.","P(Same) = 6/36 = 1/6.","P(Different) = 1 - P(Same) = 1 - 1/6 = 5/6."],"answer":"5/6"}', NULL, NULL, NULL, 18);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2A-Q02', 'cbse-10-math-full-01', 'SA', 3, 0.5, 'Find a quadratic polynomial whose zeroes are 5-3√2 and 5+3√2.', NULL, 0, NULL, NULL, '{"steps":["Sum of zeros (S) = (5-3√2) + (5+3√2) = 10","Product of zeros (P) = (5-3√2)(5+3√2)","Using (a-b)(a+b) = a² - b²","P = 25 - (9×2) = 25 - 18 = 7","Quadratic polynomial is k[x² - Sx + P]","k[x² - 10x + 7]"],"answer":"x² - 10x + 7"}', NULL, NULL, NULL, 19);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U3-B3A-Q03', 'cbse-10-math-full-01', 'SA', 3, 0.5, 'If (1, 2), (4, y), (x, 6) and (3, 5) are the vertices of a parallelogram taken in order, find x and y.', NULL, 0, NULL, NULL, '{"steps":["Diagonals of a parallelogram bisect each other.","Midpoint of AC = Midpoint of BD","Midpoint AC: ((1+x)/2, (2+6)/2) = ((1+x)/2, 4)","Midpoint BD: ((4+3)/2, (y+5)/2) = (7/2, (y+5)/2)","Comparing x-coords: (1+x)/2 = 7/2 => 1+x = 7 => x = 6","Comparing y-coords: 4 = (y+5)/2 => 8 = y+5 => y = 3"],"answer":"x = 6, y = 3"}', NULL, NULL, NULL, 20);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U5-B5A-Q02', 'cbse-10-math-full-01', 'SA', 3, 0.6, 'Prove the following trigonometric identity: (tan θ / (1 - cot θ)) + (cot θ / (1 - tan θ)) = 1 + sec θ cosec θ', 'Prove the following trigonometric identity: $\frac{\tan \theta}{1 - \cot \theta} + \frac{\cot \theta}{1 - \tan \theta} = 1 + \sec \theta \csc \theta$', 0, NULL, NULL, '{"steps":["Convert to sin/cos:","LHS = (sin/cos)/(1 - cos/sin) + (cos/sin)/(1 - sin/cos)","= (sin/cos) / ((sin-cos)/sin) + (cos/sin) / ((cos-sin)/cos)","= sin²/(cos(sin-cos)) + cos²/(sin(cos-sin))","Make denominators same (sin-cos):","= sin²/(cos(sin-cos)) - cos²/(sin(sin-cos))","= (sin³ - cos³) / (sin cos (sin-cos))","Use a³-b³ = (a-b)(a²+b²+ab)","= (sin-cos)(sin² + cos² + sin cos) / (sin cos (sin-cos))","Cancel (sin-cos):","= (1 + sin cos) / (sin cos)","= 1/(sin cos) + 1","= sec θ cosec θ + 1 = RHS"],"answer":"Proven: 1 + sec θ cosec θ"}', NULL, NULL, NULL, 21);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U4-B4A-Q02', 'cbse-10-math-full-01', 'SA', 3, 0.6, 'Diagonals AC and BD of a trapezium ABCD with AB || DC intersect each other at the point O. Using a similarity criterion for two triangles, show that OA/OC = OB/OD.', NULL, 0, NULL, NULL, '{"steps":["In ΔOAB and ΔOCD:","∠OAB = ∠OCD (Alternate interior angles, AB || DC)","∠OBA = ∠ODC (Alternate interior angles)","∠AOB = ∠COD (Vertically opposite angles)","Therefore, ΔOAB ~ ΔOCD (AAA Similarity criteria)","Sides are proportional: OA/OC = OB/OD"],"answer":"Proven using AAA similarity"}', NULL, NULL, NULL, 22);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2B-Q02', 'cbse-10-math-full-01', 'SA', 3, 0.5, 'Half the perimeter of a rectangular garden, whose length is 4 m more than its width, is 36 m. Find the dimensions of the garden.', 'Half the perimeter of a rectangular garden, whose length is $4\text{ m}$ more than its width, is $36\text{ m}$. Find the dimensions of the garden.', 0, NULL, NULL, '{"steps":["Let width = x, Length = y","y = x + 4","Perimeter P = 2(x+y). Half perimeter = x+y = 36","Substitute y: x + (x+4) = 36","2x = 32","x = 16 m","y = 16 + 4 = 20 m"],"steps_latex":["Let width $= x$, Length $= y$","$y = x + 4$","Perimeter $P = 2(x+y)$. Half perimeter $= x+y = 36$","Substitute $y$: $x + (x+4) = 36$","$2x = 32 \\Rightarrow x = 16$","Width $= 16\\text{ m}$","Length $y = 16 + 4 = 20\\text{ m}$"],"answer":"Length = 20m, Width = 16m"}', NULL, NULL, NULL, 23);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2D-Q02', 'cbse-10-math-full-01', 'SA', 3, 0.6, 'Find the sum of all two-digit numbers which when divided by 3 yield 1 as remainder.', 'Find the sum of all two-digit numbers which when divided by $3$ yield $1$ as remainder.', 0, NULL, NULL, '{"steps":["Sequence: 10, 13, 16, ..., 97","a = 10, d = 3, an = 97","an = a + (n-1)d","97 = 10 + (n-1)3","87 = 3(n-1) => 29 = n-1 => n = 30","Sum = n/2 [a + an]","S30 = 30/2 [10 + 97]","S30 = 15 [107]","S30 = 1605"],"steps_latex":["Sequence: $10, 13, 16, \\dots, 97$","$a = 10, d = 3, a_n = 97$","$a_n = a + (n-1)d$","$97 = 10 + (n-1)3$","$87 = 3(n-1) \\Rightarrow 29 = n-1 \\Rightarrow n = 30$","Sum $= \\frac{n}{2} [a + a_n]$","$S_{30} = \\frac{30}{2} [10 + 97]$","$S_{30} = 15 [107]$","$S_{30} = 1605$"],"answer":"1605"}', NULL, NULL, NULL, 24);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U5-Filler-Q39', 'cbse-10-math-full-01', 'SA', 3, 0.5, 'Prove that: (sin A + cosec A)² + (cos A + sec A)² = 7 + tan² A + cot² A', NULL, 0, NULL, NULL, '{"steps":["Expand terms:","(sin²A + cosec²A + 2) + (cos²A + sec²A + 2)","(sin²A + cos²A) + (cosec²A + sec²A) + 4","1 + (1 + cot²A) + (1 + tan²A) + 4","1 + 1 + 1 + 4 + cot²A + tan²A","7 + tan² A + cot² A"],"answer":"Proven"}', 'Trigonometry', NULL, NULL, 25);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U1-B1A-Q04', 'cbse-10-math-full-01', 'SA', 3, 0.5, 'Find the HCF and LCM of 404 and 96 and verify that HCF × LCM = Product of the two given numbers.', 'Find the HCF and LCM of $404$ and $96$ and verify that $\text{HCF} \times \text{LCM} = \text{Product of the two given numbers}$.', 0, NULL, NULL, '{"steps":["Prime factorization of 404 = 2² × 101","Prime factorization of 96 = 2^5 × 3","HCF = 2² = 4","LCM = 2^5 × 3 × 101 = 32 × 3 × 101 = 9696","Verification: HCF × LCM = 4 × 9696 = 38784","Product of numbers = 404 × 96 = 38784","Hence verified."],"steps_latex":["Prime factorization of $404 = 2^2 \\times 101$","Prime factorization of $96 = 2^5 \\times 3$","$\\text{HCF} = 2^2 = 4$","$\\text{LCM} = 2^5 \\times 3 \\times 101 = 32 \\times 3 \\times 101 = 9696$","Verification: $\\text{HCF} \\times \\text{LCM} = 4 \\times 9696 = 38784$","Product of numbers $= 404 \\times 96 = 38784$","Hence verified."],"answer":"HCF=4, LCM=9696"}', NULL, NULL, NULL, 26);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U6-B6A-Q01', 'cbse-10-math-full-01', 'SA', 3, 0.5, 'A toy is in the form of a cone of radius 3.5 cm mounted on a hemisphere of same radius. The total height of the toy is 15.5 cm. Find the total surface area of the toy.', 'A toy is in the form of a cone of radius $3.5\text{ cm}$ mounted on a hemisphere of same radius. The total height of the toy is $15.5\text{ cm}$. Find the total surface area of the toy.', 0, NULL, NULL, '{"steps":["Radius r = 3.5 cm = 7/2 cm","Total height = 15.5 cm","Height of cone h = Total - r = 15.5 - 3.5 = 12 cm","Slant height l = √(r² + h²) = √(12.25 + 144) = √156.25 = 12.5 cm","TSA = CSA of Cone + CSA of Hemisphere","TSA = πrl + 2πr²","TSA = πr(l + 2r)","TSA = (22/7) × 3.5 × (12.5 + 7)","TSA = 11 × 19.5 = 214.5 cm²"],"steps_latex":["Radius $r = 3.5\\text{ cm} = \\frac{7}{2}\\text{ cm}$","Total height $= 15.5\\text{ cm}$","Height of cone $h = \\text{Total} - r = 15.5 - 3.5 = 12\\text{ cm}$","Slant height $l = \\sqrt{r^2 + h^2} = \\sqrt{12.25 + 144} = \\sqrt{156.25} = 12.5\\text{ cm}$","TSA = CSA of Cone + CSA of Hemisphere","$\\text{TSA} = \\pi rl + 2\\pi r^2 = \\pi r(l + 2r)$","$\\text{TSA} = \\frac{22}{7} \\times 3.5 \\times (12.5 + 7)$","$\\text{TSA} = 11 \\times 19.5 = 214.5\\text{ cm}^2$"],"answer":"214.5 cm²"}', NULL, NULL, NULL, 27);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U1-B1B-Q03', 'cbse-10-math-full-01', 'SA', 3, 0.6, 'Prove that 5 - 2√3 is an irrational number, given that √3 is irrational.', NULL, 0, NULL, NULL, '{"steps":["Let 5 - 2√3 be rational equal to a/b where a,b are co-prime integers.","5 - a/b = 2√3","(5b - a)/b = 2√3","(5b - a)/2b = √3","LHS is rational because a,b are integers.","RHS (√3) is given irrational.","Rational ≠ Irrational (Contradiction).","Therefore, assumption is wrong. 5 - 2√3 is irrational."],"answer":"Proven irrational"}', NULL, NULL, NULL, 28);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U5-B5B-Q01', 'cbse-10-math-full-01', 'LA', 5, 0.6, 'As observed from the top of a 75 m high lighthouse from the sea-level, the angles of depression of two ships are 30° and 45°. If one ship is exactly behind the other on the same side of the lighthouse, find the distance between the two ships.', NULL, 1, '<svg viewBox=''0 0 320 190'' xmlns=''http://www.w3.org/2000/svg''><line x1=''60'' y1=''20'' x2=''60'' y2=''150'' stroke=''black'' stroke-width=''5''/><line x1=''60'' y1=''150'' x2=''300'' y2=''150'' stroke=''blue'' stroke-width=''3''/><line x1=''60'' y1=''20'' x2=''160'' y2=''150'' stroke=''black'' stroke-dasharray=''5 3'' stroke-width=''2''/><line x1=''60'' y1=''20'' x2=''260'' y2=''150'' stroke=''black'' stroke-dasharray=''5 3'' stroke-width=''2''/><text x=''50'' y=''25'' font-size=''14'' font-weight=''bold''>A</text><text x=''50'' y=''155'' font-size=''14'' font-weight=''bold''>B</text><text x=''160'' y=''175'' font-size=''13'' text-anchor=''middle''>D (45°)</text><text x=''260'' y=''175'' font-size=''13'' text-anchor=''middle''>C (30°)</text></svg>', NULL, '{"steps":["Let lighthouse be AB = 75m.","Let ships be at points C and D (D is closer).","Angle of elevation for D = 45° (Alt interior to depression)","Angle of elevation for C = 30°","In ΔABD: tan 45° = AB/BD => 1 = 75/BD => BD = 75m","In ΔABC: tan 30° = AB/BC => 1/√3 = 75/BC => BC = 75√3 m","Distance between ships CD = BC - BD","CD = 75√3 - 75 = 75(√3 - 1) m","Using √3 ≈ 1.732: 75(0.732) = 54.9 m"],"answer":"75(√3 - 1) m"}', NULL, NULL, NULL, 29);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-B2C-Q02', 'cbse-10-math-full-01', 'LA', 5, 0.7, 'A motor boat whose speed is 18 km/h in still water takes 1 hour more to go 24 km upstream than to return downstream to the same spot. Find the speed of the stream.', 'A motor boat whose speed is $18\text{ km/h}$ in still water takes $1\text{ hour}$ more to go $24\text{ km}$ upstream than to return downstream to the same spot. Find the speed of the stream.', 0, NULL, NULL, '{"steps":["Speed of boat = 18 km/h. Let speed of stream = x km/h.","Upstream speed = 18-x. Downstream speed = 18+x.","Time Up = 24/(18-x). Time Down = 24/(18+x).","Difference: 24/(18-x) - 24/(18+x) = 1","24 [ (18+x - (18-x)) / (18² - x²) ] = 1","24 [ 2x ] = 324 - x²","48x = 324 - x²","x² + 48x - 324 = 0","Using Quadratic formula: D = 48² - 4(1)(-324) = 2304 + 1296 = 3600","√D = 60","x = (-48 ± 60) / 2","x = 12/2 = 6 or x = -108/2 (Reject negative)","Speed of stream = 6 km/h"],"steps_latex":["Speed of boat $= 18\\text{ km/h}$. Let speed of stream $= x\\text{ km/h}$","Upstream speed $= 18-x$. Downstream speed $= 18+x$","Time Up $= \\frac{24}{18-x}$. Time Down $= \\frac{24}{18+x}$","Difference: $\\frac{24}{18-x} - \\frac{24}{18+x} = 1$","$24 [ \\frac{18+x - (18-x)}{18^2 - x^2} ] = 1$","$24 [ \\frac{2x}{324 - x^2} ] = 1$","$48x = 324 - x^2$","$x^2 + 48x - 324 = 0$","Using Quadratic formula: $D = 48^2 - 4(1)(-324) = 2304 + 1296 = 3600$","$\\sqrt{D} = 60$","$x = \\frac{-48 \\pm 60}{2}$","$x = \\frac{12}{2} = 6$ or $x = -54$ (Reject negative)","Speed of stream $= 6\\text{ km/h}$"],"answer":"6 km/h"}', NULL, NULL, NULL, 30);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U4-B4B-Q02', 'cbse-10-math-full-01', 'LA', 5, 0.7, 'A triangle ABC is drawn to circumscribe a circle of radius 4 cm such that the segments BD and DC into which BC is divided by the point of contact D are of lengths 8 cm and 6 cm respectively. Find the sides AB and AC.', NULL, 1, '<svg viewBox=''0 0 200 180'' xmlns=''http://www.w3.org/2000/svg''><circle cx=''100'' cy=''90'' r=''30'' stroke=''black'' fill=''none'' stroke-width=''2''/><path d=''M 10,150 L 190,150 L 100,10 Z'' stroke=''black'' fill=''none'' stroke-width=''2''/><text x=''100'' y=''5'' font-size=''12'' text-anchor=''middle''>A</text><text x=''5'' y=''155'' font-size=''12'' text-anchor=''middle''>B</text><text x=''195'' y=''155'' font-size=''12'' text-anchor=''middle''>C</text><text x=''100'' y=''165'' font-size=''12'' text-anchor=''middle''>D</text><line x1=''100'' y1=''90'' x2=''100'' y2=''150'' stroke=''black'' stroke-dasharray=''4''/></svg>', NULL, '{"steps":["Let point of contacts be E on AC and F on AB.","CD = CE = 6 cm, BD = BF = 8 cm (Tangents from external point)","Let AF = AE = x","Sides: a = 14, b = x+6, c = x+8","s = (14 + x + 6 + x + 8)/2 = 14 + x","Area = √(s(s-a)(s-b)(s-c)) = √((14+x)(x)(8)(6)) = √(48x(14+x))","Also Area = Area(OBC) + Area(OCA) + Area(OAB) = 1/2(4)(14 + x+6 + x+8)","Area = 2(28 + 2x) = 4(14+x)","Equating: 16(14+x)² = 48x(14+x)","Divide by 16(14+x): (14+x) = 3x => 2x = 14 => x = 7","AB = x + 8 = 15 cm","AC = x + 6 = 13 cm"],"answer":"AB = 15 cm, AC = 13 cm"}', NULL, NULL, NULL, 31);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U4-CS-Q36', 'cbse-10-math-full-01', 'CaseStudy', 4, 0.6, '', NULL, 0, NULL, NULL, '{}', 'Geometry', 'Triangles', '{"title":"Kite Festival","context_plain":"During a kite festival, a kite is flying at a height of 60m above the ground. The string attached to the kite is temporarily tied to a point on the ground. The inclination of the string with the ground is 60°. At the same time, another kite is flying such that its string makes an angle of 45° with the ground.","has_diagram":true,"visual_diagram":{"diagram_type":"geometric","svg_code":"<svg viewBox=''0 0 300 200'' xmlns=''http://www.w3.org/2000/svg''>\n  <!-- Sky Background -->\n  <rect width=''300'' height=''200'' fill=''#f0f8ff''/>\n  <!-- Ground -->\n  <rect y=''180'' width=''300'' height=''20'' fill=''#90ee90''/>\n  \n  <!-- Kite String -->\n  <line x1=''50'' y1=''180'' x2=''200'' y2=''50'' stroke=''#555'' stroke-width=''2'' stroke-dasharray=''5,5''/>\n  \n  <!-- Kite Body -->\n  <path d=''M200,50 L220,70 L200,100 L180,70 Z'' fill=''#ff6b6b'' stroke=''#c0392b'' stroke-width=''2''/>\n  <line x1=''180'' y1=''70'' x2=''220'' y2=''70'' stroke=''#c0392b''/>\n  <line x1=''200'' y1=''50'' x2=''200'' y2=''100'' stroke=''#c0392b''/>\n  \n  <!-- Measurements -->\n  <text x=''110'' y=''110'' font-family=''Arial'' font-size=''14'' fill=''#333''>Length L</text>\n  <text x=''240'' y=''80'' font-family=''Arial'' font-size=''14'' fill=''#333''>Height 60m</text>\n  \n  <!-- Height Line -->\n  <line x1=''200'' y1=''50'' x2=''200'' y2=''180'' stroke=''#333'' stroke-dasharray=''2,2''/>\n  \n  <!-- Angle -->\n  <path d=''M80,180 A30,30 0 0,0 75,158'' fill=''none'' stroke=''black''/>\n  <text x=''90'' y=''170'' font-size=''12''>60°</text>\n</svg>","description":"Two triangles representing kite strings"},"sub_questions":[{"part":"i","marks":1,"question":{"text_plain":"Find the length of the string of the first kite (assuming no slack)."},"answer":{"correct_answer_plain":"40√3 m"},"solution":{"steps":["Height h = 60.","Angle = 60°.","sin 60° = h/L","√3/2 = 60/L","L = 120/√3 = 40√3 m"]}},{"part":"ii","marks":1,"question":{"text_plain":"If the second kite is also at height 60m, find its string length."},"answer":{"correct_answer_plain":"60√2 m"},"solution":{"steps":["sin 45° = 60/L","1/√2 = 60/L","L = 60√2 m"]}},{"part":"iii","marks":2,"question":{"text_plain":"If the two kites are on opposite sides of the tying point, find the distance between the kites along the horizontal ground."},"answer":{"correct_answer_plain":"60(1 + 1/√3) m"},"solution":{"steps":["Base1 (tan 60) = 60/B1 => √3 = 60/B1 => B1 = 20√3.","Base2 (tan 45) = 60/B2 => 1 = 60/B2 => B2 = 60.","Total Dist = 60 + 20√3 = 20(3 + √3) m."]}}]}', 32);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U2-CS-Q37', 'cbse-10-math-full-01', 'CaseStudy', 4, 0.5, '', NULL, 0, NULL, NULL, '{}', 'Algebra', 'Arithmetic Progressions', '{"title":"Savings Scheme","context_plain":"Reena starts a savings scheme. She saves ₹100 in the first month, ₹120 in the second month, ₹140 in the third month, and so on. Her savings increase by a fixed amount every month.","has_diagram":false,"visual_diagram":null,"sub_questions":[{"part":"i","marks":1,"question":{"text_plain":"Find the amount she saves in the 10th month."},"answer":{"correct_answer_plain":"₹280"},"solution":{"steps":["a=100, d=20.","a10 = a+9d","100 + 9(20) = 280"]}},{"part":"ii","marks":1,"question":{"text_plain":"Find the total savings in 12 months."},"answer":{"correct_answer_plain":"₹2520"},"solution":{"steps":["S12 = 12/2 [2a + 11d]","6 [200 + 220]","6 [420] = 2520"]}},{"part":"iii","marks":2,"question":{"text_plain":"In which month will her monthly saving be ₹400?"},"answer":{"correct_answer_plain":"16th Month"},"solution":{"steps":["an = 400","a + (n-1)d = 400","100 + (n-1)20 = 400","(n-1)20 = 300","n-1 = 15","n = 16"]}}]}', 33);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-01-U7-CS-Q38', 'cbse-10-math-full-01', 'CaseStudy', 4, 0.4, '', NULL, 0, NULL, NULL, '{}', 'Statistics & Probability', 'Probability', '{"title":"Blood Donation Camp","context_plain":"In a blood donation camp, 50 donors participated. Their blood groups are recorded: 20 have Type A, 15 have Type B, 10 have Type O, and 5 have Type AB.","has_diagram":false,"visual_diagram":{"diagram_type":"geometric","svg_code":"<svg viewBox=''0 0 300 150'' xmlns=''http://www.w3.org/2000/svg''>\n  <rect width=''300'' height=''150'' fill=''#fff0f0''/>\n  \n  <!-- Drops -->\n  <path transform=''translate(50, 40) scale(2)'' d=''M10,0 C10,0 0,6 0,12 A10,10 0 1,0 20,12 C20,6 10,0 10,0'' fill=''#ff0000''/>\n  <text x=''60'' y=''100'' text-anchor=''middle'' font-weight=''bold''>Type A</text>\n  <text x=''60'' y=''120'' text-anchor=''middle''>20</text>\n  \n  <path transform=''translate(150, 40) scale(2)'' d=''M10,0 C10,0 0,6 0,12 A10,10 0 1,0 20,12 C20,6 10,0 10,0'' fill=''#cc0000''/>\n  <text x=''160'' y=''100'' text-anchor=''middle'' font-weight=''bold''>Type B</text>\n  <text x=''160'' y=''120'' text-anchor=''middle''>15</text>\n  \n  <path transform=''translate(250, 40) scale(2)'' d=''M10,0 C10,0 0,6 0,12 A10,10 0 1,0 20,12 C20,6 10,0 10,0'' fill=''#990000''/>\n  <text x=''260'' y=''100'' text-anchor=''middle'' font-weight=''bold''>Type O</text>\n  <text x=''260'' y=''120'' text-anchor=''middle''>10</text>\n</svg>","description":null},"sub_questions":[{"part":"i","marks":1,"question":{"text_plain":"Find the probability that a randomly selected donor has Type O blood."},"answer":{"correct_answer_plain":"1/5"},"solution":{"steps":["Total = 50. Type O = 10.","P(O) = 10/50 = 1/5"]}},{"part":"ii","marks":1,"question":{"text_plain":"Find the probability that a donor has Type A or Type B blood."},"answer":{"correct_answer_plain":"7/10"},"solution":{"steps":["Type A + Type B = 20 + 15 = 35.","P(A or B) = 35/50 = 7/10"]}},{"part":"iii","marks":2,"question":{"text_plain":"What is the probability that a donor does NOT have Type AB?"},"answer":{"correct_answer_plain":"9/10"},"solution":{"steps":["P(Not AB) = 1 - P(AB)","Type AB = 5.","P(AB) = 5/50 = 1/10.","1 - 1/10 = 9/10"]}}]}', 34);
