INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2A-Q01', 'cbse-10-math-full-02', 'MCQ', 1, 0.4, 'If the sum of the zeroes of the quadratic polynomial kx² + 2x + 3k is equal to their product, then k equals:', 'If the sum of the zeroes of the quadratic polynomial $kx^2 + 2x + 3k$ is equal to their product, then $k$ equals:', 0, NULL, '[{"id":"A","text":"1/3","is_correct":false},{"id":"B","text":"-1/3","is_correct":false},{"id":"C","text":"-2/3","is_correct":true},{"id":"D","text":"2/3","is_correct":false}]', '{"steps":["Sum of zeroes (α+β) = -b/a = -2/k","Product of zeroes (αβ) = c/a = 3k/k = 3","Given Sum = Product => -2/k = 3","k = -2/3"],"steps_latex":["Sum of zeroes $(\\alpha+\\beta) = -\\frac{b}{a} = -\\frac{2}{k}$","Product of zeroes $(\\alpha\\beta) = \\frac{c}{a} = \\frac{3k}{k} = 3$","Given Sum = Product $\\Rightarrow -\\frac{2}{k} = 3$","$k = -\\frac{2}{3}$"],"answer":{"correct_option":"C","correct_answer_latex":"$-\\frac{2}{3}$"}}', NULL, NULL, NULL, 1);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U3-B3A-Q01', 'cbse-10-math-full-02', 'MCQ', 1, 0.4, 'The distance of the point P(2, 3) from the x-axis is:', 'The distance of the point $P(2, 3)$ from the $x$-axis is:', 0, NULL, '[{"id":"A","text":"2","is_correct":false},{"id":"B","text":"3","is_correct":true},{"id":"C","text":"1","is_correct":false},{"id":"D","text":"5","is_correct":false}]', '{"steps":["Distance from x-axis is the absolute value of the y-coordinate.","y-coordinate = 3","Distance = 3 units"],"steps_latex":["Distance of a point $(x, y)$ from $x$-axis is $|y|$.","Here $y = 3$.","Distance $= 3$ units"],"answer":{"correct_option":"B","correct_answer_latex":"$3$"}}', NULL, NULL, NULL, 2);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U5-B5A-Q01', 'cbse-10-math-full-02', 'MCQ', 1, 0.4, 'If tan A = 4/3, then sin A is:', 'If $\tan A = \frac{4}{3}$, then $\sin A$ is:', 0, NULL, '[{"id":"A","text":"3/5","is_correct":false},{"id":"B","text":"4/5","is_correct":true},{"id":"C","text":"3/4","is_correct":false},{"id":"D","text":"4/3","is_correct":false}]', '{"steps":["tan A = P/B = 4/3","Let P = 4k, B = 3k","H^2 = P^2 + B^2 = 16k^2 + 9k^2 = 25k^2","H = 5k","sin A = P/H = 4k/5k = 4/5"],"steps_latex":["$\\tan A = \\frac{P}{B} = \\frac{4}{3}$","Let $P = 4k, B = 3k$","$H^2 = P^2 + B^2 = (4k)^2 + (3k)^2 = 25k^2$","$H = 5k$","$\\sin A = \\frac{P}{H} = \\frac{4k}{5k} = \\frac{4}{5}$"],"answer":{"correct_option":"B","correct_answer_latex":"$\\frac{4}{5}$"}}', NULL, NULL, NULL, 3);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U4-B4A-Q01', 'cbse-10-math-full-02', 'MCQ', 1, 0.5, 'In triangle ABC, DE || BC. If AD = 1.5 cm, DB = 3 cm, AE = 1 cm, then EC is:', 'In $\triangle ABC$, $DE \parallel BC$. If $AD = 1.5\text{ cm}, DB = 3\text{ cm}, AE = 1\text{ cm}$, then $EC$ is:', 1, '<svg viewBox=''0 0 200 200''><polygon points=''100,20 40,180 160,180'' stroke=''black'' fill=''none'' stroke-width=''2''/><line x1=''70'' y1=''100'' x2=''130'' y2=''100'' stroke=''black'' stroke-width=''2''/><text x=''100'' y=''15'' font-size=''12''>A</text><text x=''30'' y=''190'' font-size=''12''>B</text><text x=''170'' y=''190'' font-size=''12''>C</text><text x=''60'' y=''100'' font-size=''12''>D</text><text x=''135'' y=''100'' font-size=''12''>E</text></svg>', '[{"id":"A","text":"2 cm","is_correct":true},{"id":"B","text":"3 cm","is_correct":false},{"id":"C","text":"4 cm","is_correct":false},{"id":"D","text":"2.5 cm","is_correct":false}]', '{"steps":["By BPT: AD/DB = AE/EC","1.5/3 = 1/EC","1/2 = 1/EC","EC = 2 cm"],"steps_latex":["By Basic Proportionality Theorem (BPT): $\\frac{AD}{DB} = \\frac{AE}{EC}$","$\\frac{1.5}{3} = \\frac{1}{EC}$","$\\frac{1}{2} = \\frac{1}{EC}$","$EC = 2\\text{ cm}$"],"answer":{"correct_option":"A","correct_answer_latex":"$2\\text{ cm}$"}}', NULL, NULL, NULL, 4);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2B-Q01', 'cbse-10-math-full-02', 'MCQ', 1, 0.4, 'The pair of equations x + 2y + 5 = 0 and -3x - 6y + 1 = 0 have:', 'The pair of equations $x + 2y + 5 = 0$ and $-3x - 6y + 1 = 0$ have:', 0, NULL, '[{"id":"A","text":"a unique solution","is_correct":false},{"id":"B","text":"exactly two solutions","is_correct":false},{"id":"C","text":"infinitely many solutions","is_correct":false},{"id":"D","text":"no solution","is_correct":true}]', '{"steps":["a1/a2 = 1/-3","b1/b2 = 2/-6 = 1/-3","c1/c2 = 5/1","a1/a2 = b1/b2 ≠ c1/c2 => Parallel lines => No solution"],"steps_latex":["$\\frac{a_1}{a_2} = \\frac{1}{-3} = -\\frac{1}{3}$","$\\frac{b_1}{b_2} = \\frac{2}{-6} = -\\frac{1}{3}$","$\\frac{c_1}{c_2} = \\frac{5}{1} = 5$","Since $\\frac{a_1}{a_2} = \\frac{b_1}{b_2} \\neq \\frac{c_1}{c_2}$, lines are parallel.","Therefore, the system has **No solution**."],"answer":{"correct_option":"D","correct_answer_latex":"\\text{No solution}"}}', NULL, NULL, NULL, 5);
