INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U4-Gap-Q09', 'cbse-10-math-full-02', 'MCQ', 1, 0.4, 'From a point Q, the length of the tangent to a circle is 24 cm and the distance of Q from the centre is 25 cm. The radius of the circle is:', 'From a point $Q$, the length of the tangent to a circle is $24\text{ cm}$ and the distance of $Q$ from the centre is $25\text{ cm}$. The radius of the circle is:', 0, NULL, '[{"id":"A","text":"7 cm","is_correct":true},{"id":"B","text":"12 cm","is_correct":false},{"id":"C","text":"15 cm","is_correct":false},{"id":"D","text":"24.5 cm","is_correct":false}]', '{"steps_latex":["$r^2 + 24^2 = 25^2$ (Pythagoras theorem in tangent triangle)","$r^2 + 576 = 625$","$r^2 = 49 \\Rightarrow r = 7\\text{ cm}$"]}', NULL, NULL, NULL, 19);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U7-B7B-Q01', 'cbse-10-math-full-02', 'MCQ', 1, 0.4, 'A card is drawn from a well-shuffled deck of 52 cards. The probability that the card is a face card is:', 'A card is drawn from a well-shuffled deck of $52$ cards. The probability that the card is a face card is:', 0, NULL, '[{"id":"A","text":"3/13","is_correct":true},{"id":"B","text":"1/4","is_correct":false},{"id":"C","text":"1/13","is_correct":false},{"id":"D","text":"4/13","is_correct":false}]', '{"steps":["Total cards = 52","Face cards (J, Q, K) in 4 suits = 3 * 4 = 12","Probability = 12/52 = 3/13"],"steps_latex":["Total number of outcomes $= 52$","Number of face cards (King, Queen, Jack in 4 suits) $= 3 \\times 4 = 12$","Probability $P(E) = \\frac{12}{52} = \\frac{3}{13}$"],"answer":{"correct_option":"A","correct_answer_latex":"$\\frac{3}{13}$"}}', NULL, NULL, NULL, 20);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2A-Q02', 'cbse-10-math-full-02', 'SA', 2, 0.5, 'Find a quadratic polynomial whose zeroes are 5+2√3 and 5-2√3.', 'Find a quadratic polynomial whose zeroes are $5+2\sqrt{3}$ and $5-2\sqrt{3}$.', 0, NULL, NULL, '{"steps":["Sum = (5+2√3) + (5-2√3) = 10","Product = (5+2√3)(5-2√3) = 25 - 12 = 13","Polynomial = k[x² - (Sum)x + Product]","P(x) = x² - 10x + 13"],"steps_latex":["Sum $= (5+2\\sqrt{3}) + (5-2\\sqrt{3}) = 10$","Product $= (5+2\\sqrt{3})(5-2\\sqrt{3}) = 5^2 - (2\\sqrt{3})^2 = 25 - 12 = 13$","Polynomial $= k[x^2 - (\\text{Sum})x + \\text{Product}]$","$P(x) = x^2 - 10x + 13$"],"answer":{"correct_answer_plain":"x² - 10x + 13","correct_answer_latex":"$x^2 - 10x + 13$"}}', NULL, NULL, NULL, 21);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2C-Q01', 'cbse-10-math-full-02', 'SA', 2, 0.5, 'Find the discriminant of the quadratic equation 2x² - 4x + 3 = 0, and hence find the nature of its roots.', 'Find the discriminant of the quadratic equation $2x^2 - 4x + 3 = 0$, and hence find the nature of its roots.', 0, NULL, NULL, '{"steps":["Equation: 2x² - 4x + 3 = 0","a=2, b=-4, c=3","Discriminant D = b² - 4ac","D = (-4)² - 4(2)(3) = 16 - 24 = -8","Since D < 0, roots are imaginary/no real roots."],"steps_latex":["Compare with $ax^2 + bx + c = 0$: $a=2, b=-4, c=3$","Discriminant $D = b^2 - 4ac$","$D = (-4)^2 - 4(2)(3)$","$D = 16 - 24 = -8$","Since $D < 0$, the equation has **no real roots**."],"answer":{"correct_answer_plain":"D = -8, No real roots","correct_answer_latex":"D = -8, \\text{No real roots}"}}', NULL, NULL, NULL, 22);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U1-B1A-Q03', 'cbse-10-math-full-02', 'SA', 2, 0.5, 'Prove that 3 + 2√5 is irrational, given that √5 is irrational.', 'Prove that $3 + 2\sqrt{5}$ is irrational, given that $\sqrt{5}$ is irrational.', 0, NULL, NULL, '{"steps":["Let 3 + 2√5 be rational r.","2√5 = r - 3","√5 = (r - 3)/2","RHS is rational, LHS is irrational. Contradiction.","Hence 3 + 2√5 is irrational."],"steps_latex":["Let $3 + 2\\sqrt{5}$ be rational number $r$.","$2\\sqrt{5} = r - 3$","$\\sqrt{5} = \\frac{r - 3}{2}$","RHS is rational (since $r$ is rational), but LHS ($\\sqrt{5}$) is irrational.","This is a contradiction.","Hence, $3 + 2\\sqrt{5}$ is irrational."],"answer":{"correct_answer_plain":"Proof completed","correct_answer_latex":"\\text{Proof completed}"}}', NULL, NULL, NULL, 23);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U3-Gap-Q10', 'cbse-10-math-full-02', 'SA', 2, 0.4, 'Find the distance between the points (0, 0) and (36, 15).', 'Find the distance between the points $(0, 0)$ and $(36, 15)$.', 0, NULL, NULL, '{"steps":["Distance Formula: sqrt((x2-x1)^2 + (y2-y1)^2)","d = sqrt(36^2 + 15^2)","d = sqrt(1296 + 225) = sqrt(1521)","d = 39 units"],"steps_latex":["Distance $d = \\sqrt{(x_2-x_1)^2 + (y_2-y_1)^2}$","$d = \\sqrt{(36-0)^2 + (15-0)^2}$","$d = \\sqrt{1296 + 225} = \\sqrt{1521}$","$d = 39\\text{ units}$"],"answer":{"correct_answer_plain":"39 units","correct_answer_latex":"$39\\text{ units}$"}}', NULL, NULL, NULL, 24);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U1-B1B-Q01', 'cbse-10-math-full-02', 'SA', 2, 0.5, 'Prove that 5 - √3 is an irrational number, given that √3 is irrational.', 'Prove that $5 - \sqrt{3}$ is an irrational number, given that $\sqrt{3}$ is irrational.', 0, NULL, NULL, '{"steps":["Assume 5 - √3 is rational, say r.","5 - r = √3","RHS (√3) is irrational.","LHS (5 - r) is rational because difference of rational numbers is rational.","Rational = Irrational is a contradiction.","Therefore, 5 - √3 is irrational."],"steps_latex":["Assume $5 - \\sqrt{3}$ is rational, say $r$.","$5 - r = \\sqrt{3}$","RHS ($\\sqrt{3}$) is irrational.","LHS ($5 - r$) is rational because difference of rational numbers is rational.","Rational $\\neq$ Irrational. This is a contradiction.","Therefore, $5 - \\sqrt{3}$ is irrational."],"answer":{"correct_answer_plain":"Proved","correct_answer_latex":"\\text{Proved}"}}', NULL, NULL, NULL, 25);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U3-B3A-Q02', 'cbse-10-math-full-02', 'SA', 3, 0.5, 'Find the ratio in which the line segment joining the points (-3, 10) and (6, -8) is divided by (-1, 6).', 'Find the ratio in which the line segment joining the points $(-3, 10)$ and $(6, -8)$ is divided by $(-1, 6)$.', 0, NULL, NULL, '{"steps":["Let ratio be k:1.","Using Section Formula: x = (m1x2 + m2x1)/(m1+m2)","-1 = (6k - 3) / (k + 1)","-1(k+1) = 6k - 3","-k - 1 = 6k - 3","2 = 7k => k = 2/7","Ratio is 2:7"],"steps_latex":["Let the ratio be $k:1$. Points $A(-3, 10), B(6, -8)$.","Using Section Formula for x-coordinate: $x = \\frac{m_1x_2 + m_2x_1}{m_1+m_2}$","$-1 = \\frac{k(6) + 1(-3)}{k+1}$","$-k - 1 = 6k - 3$","$2 = 7k \\Rightarrow k = \\frac{2}{7}$","Therefore, the ratio is $2:7$."],"answer":{"correct_answer_plain":"2:7","correct_answer_latex":"$2:7$"}}', NULL, NULL, NULL, 26);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U5-B5A-Q02', 'cbse-10-math-full-02', 'SA', 3, 0.6, 'Prove that: (sin A / (1 + cos A)) + ((1 + cos A) / sin A) = 2 cosec A', 'Prove that: $\frac{\sin A}{1 + \cos A} + \frac{1 + \cos A}{\sin A} = 2 \csc A$', 0, NULL, NULL, '{"steps":["LHS = (sin^2 A + (1+cos A)^2) / (sin A(1+cos A))","= (sin^2 A + 1 + 2cos A + cos^2 A) / (sin A(1+cos A))","= (1 + 1 + 2cos A) / (sin A(1+cos A))","= (2 + 2cos A) / (sin A(1+cos A))","= 2(1 + cos A) / (sin A(1+cos A))","= 2/sin A = 2 cosec A = RHS"],"steps_latex":["LHS $= \\frac{\\sin^2 A + (1+\\cos A)^2}{\\sin A(1+\\cos A)}$","$= \\frac{\\sin^2 A + 1 + 2\\cos A + \\cos^2 A}{\\sin A(1+\\cos A)}$","Using $\\sin^2 A + \\cos^2 A = 1$:","$= \\frac{1 + 1 + 2\\cos A}{\\sin A(1+\\cos A)}$","$= \\frac{2(1 + \\cos A)}{\\sin A(1+\\cos A)}$","$= \\frac{2}{\\sin A} = 2 \\csc A = \\text{RHS}$"],"answer":{"correct_answer_plain":"Proved","correct_answer_latex":"\\text{Proved}"}}', NULL, NULL, NULL, 27);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2D-Q02', 'cbse-10-math-full-02', 'SA', 3, 0.5, 'Find the sum of first 22 terms of an AP in which d = 7 and 22nd term is 149.', 'Find the sum of first $22$ terms of an AP in which $d = 7$ and $22^{\text{nd}}$ term is $149$.', 0, NULL, NULL, '{"steps":["d = 7, n = 22, a22 = 149","a22 = a + 21d => 149 = a + 21(7)","149 = a + 147 => a = 2","S22 = n/2 (a + an)","S22 = 22/2 (2 + 149) = 11 (151) = 1661"],"steps_latex":["$d = 7, n = 22, a_{22} = 149$","$a_{22} = a + 21d \\Rightarrow 149 = a + 21(7)$","$149 = a + 147 \\Rightarrow a = 2$","$S_{22} = \\frac{n}{2} (a + a_{22})$","$S_{22} = \\frac{22}{2} (2 + 149) = 11 \\times 151 = 1661$"],"answer":{"correct_answer_plain":"1661","correct_answer_latex":"$1661$"}}', NULL, NULL, NULL, 28);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U4-B4B-Q01', 'cbse-10-math-full-02', 'SA', 3, 0.5, 'Prove that the lengths of tangents drawn from an external point to a circle are equal.', 'Prove that the lengths of tangents drawn from an external point to a circle are equal.', 1, '<svg viewBox=''0 0 200 150''><circle cx=''130'' cy=''75'' r=''40'' stroke=''black'' fill=''none'' stroke-width=''2''/><path d=''M20,75 L110,48 M20,75 L110,102'' stroke=''black'' stroke-width=''2''/><line x1=''130'' y1=''75'' x2=''110'' y2=''48'' stroke=''black'' stroke-dasharray=''4'' /><line x1=''130'' y1=''75'' x2=''110'' y2=''102'' stroke=''black'' stroke-dasharray=''4'' /><line x1=''20'' y1=''75'' x2=''130'' y2=''75'' stroke=''black'' stroke-dasharray=''4'' /><text x=''13'' y=''75''>P</text><text x=''112'' y=''45''>A</text><text x=''112'' y=''115''>B</text><text x=''140'' y=''75''>O</text></svg>', NULL, '{"steps":["Given: Circle with center O, tangents PA and PB from P.","To prove: PA = PB","Construction: Join OA, OB, OP.","Proof: In triangles OAP and OBP","angle OAP = angle OBP = 90 deg (Radius perp to tangent)","OP = OP (Common)","OA = OB (Radii)","By RHS congruence, Triangle OAP ~= Triangle OBP","By CPCT, PA = PB."],"steps_latex":["**Given:** A circle with center $O$, and two tangents $PA$ and $PB$ from external point $P$.","**To Prove:** $PA = PB$","**Construction:** Join $OA, OB$ and $OP$.","**Proof:** In $\\triangle OAP$ and $\\triangle OBP$:","$\\angle OAP = \\angle OBP = 90^\\circ$ (Tangent is $\\perp$ to radius)","$OP = OP$ (Common hypotenuse)","$OA = OB$ (Radii)","$\\therefore \\triangle OAP \\cong \\triangle OBP$ (RHS Congruence)","$\\Rightarrow PA = PB$ (CPCT)"],"answer":{"correct_answer_plain":"Proved","correct_answer_latex":"\\text{Proved}"}}', NULL, NULL, NULL, 29);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U1-B1B-Q02', 'cbse-10-math-full-02', 'SA', 3, 0.4, 'Three bells ring at intervals of 4, 7 and 14 minutes. All three rang at 6 AM. When will they ring together again?', 'Three bells ring at intervals of $4, 7$ and $14$ minutes. All three rang at $6\text{ AM}$. When will they ring together again?', 0, NULL, NULL, '{"steps":["Find LCM of 4, 7, 14.","4 = 2^2, 7 = 7^1, 14 = 2 × 7","LCM = 2^2 × 7 = 4 × 7 = 28 minutes","Time = 6:00 AM + 28 mins = 6:28 AM"],"steps_latex":["Find $\\text{LCM}(4, 7, 14)$.","$4 = 2^2, 7 = 7^1, 14 = 2 \\times 7$","$\\text{LCM} = 2^2 \\times 7 = 4 \\times 7 = 28\\text{ minutes}$","Time $= 6:00\\text{ AM} + 28\\text{ mins} = 6:28\\text{ AM}$"],"answer":{"correct_answer_plain":"6:28 AM","correct_answer_latex":"6:28\\text{ AM}"}}', NULL, NULL, NULL, 30);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U6-B6B-Q01', 'cbse-10-math-full-02', 'SA', 3, 0.5, 'A metallic sphere of radius 4.2 cm is melted and recast into the shape of a cylinder of radius 6 cm. Find the height of the cylinder.', 'A metallic sphere of radius $4.2\text{ cm}$ is melted and recast into the shape of a cylinder of radius $6\text{ cm}$. Find the height of the cylinder.', 0, NULL, NULL, '{"steps":["Volume of Sphere = Volume of Cylinder","4/3 pi R^3 = pi r^2 h","4/3 * (4.2)^3 = (6)^2 * h","h = (4/3 * 4.2 * 4.2 * 4.2) / 36","h = 1.4 * 1.4 * 1.4 = 2.744 cm"],"steps_latex":["Volume of Sphere = Volume of Cylinder","$\\frac{4}{3} \\pi R^3 = \\pi r^2 h$","$\\frac{4}{3} \\times (4.2)^3 = (6)^2 \\times h$","$h = \\frac{4}{3} \\times \\frac{4.2 \\times 4.2 \\times 4.2}{36}$","$h = \\frac{4 \\times 1.4 \\times 0.7 \\times 0.7}{1}$ (simplified)","$h = 2.744\\text{ cm}$"],"answer":{"correct_answer_plain":"2.74 cm","correct_answer_latex":"$2.744\\text{ cm}$"}}', NULL, NULL, NULL, 31);
