INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2B-Q02', 'cbse-10-math-full-02', 'LA', 5, 0.7, '2 women and 5 men can together finish an embroidery work in 4 days, while 3 women and 6 men can finish it in 3 days. Find the time taken by 1 woman alone to finish the work, and also that taken by 1 man alone.', '$2$ women and $5$ men can together finish an embroidery work in $4$ days, while $3$ women and $6$ men can finish it in $3$ days. Find the time taken by $1$ woman alone to finish the work, and also that taken by $1$ man alone.', 0, NULL, NULL, '{"steps":["Let work done by 1 woman in 1 day = 1/x","Let work done by 1 man in 1 day = 1/y","Case 1: 4(2/x + 5/y) = 1 => 2/x + 5/y = 1/4","Case 2: 3(3/x + 6/y) = 1 => 3/x + 6/y = 1/3","Let 1/x = u, 1/y = v. 2u + 5v = 1/4 (1), 3u + 6v = 1/3 (2)","Solve linear equations...","Multiply (1) by 3: 6u + 15v = 3/4","Multiply (2) by 2: 6u + 12v = 2/3","Subtract: 3v = 3/4 - 2/3 = (9-8)/12 = 1/12","v = 1/36 => y = 36 days","Sub v in (1): 2u + 5(1/36) = 1/4 => 2u = 1/4 - 5/36 = (9-5)/36 = 4/36 = 1/9","2u = 1/9 => u = 1/18 => x = 18 days"],"steps_latex":["Let time taken by 1 woman $= x$ days, by 1 man $= y$ days.","$\\frac{2}{x} + \\frac{5}{y} = \\frac{1}{4}$ ... (i)","$\\frac{3}{x} + \\frac{6}{y} = \\frac{1}{3}$ ... (ii)","Let $\\frac{1}{x} = u, \\frac{1}{y} = v$. System becomes:","$2u + 5v = \\frac{1}{4}$","$3u + 6v = \\frac{1}{3}$","Multiply (i) by 3: $6u + 15v = \\frac{3}{4}$","Multiply (ii) by 2: $6u + 12v = \\frac{2}{3}$","Subtracting: $3v = \\frac{3}{4} - \\frac{2}{3} = \\frac{9-8}{12} = \\frac{1}{12} \\Rightarrow v = \\frac{1}{36}$","Substitute $v$: $2u + \\frac{5}{36} = \\frac{1}{4} \\Rightarrow 2u = \\frac{9-5}{36} = \\frac{1}{9} \\Rightarrow u = \\frac{1}{18}$","So $x = 18$ days, $y = 36$ days."],"answer":{"correct_answer_plain":"Woman: 18 days, Man: 36 days","correct_answer_latex":"\\text{Woman: } 18 \\text{ days, Man: } 36 \\text{ days}"}}', NULL, NULL, NULL, 34);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U5-B5B-Q01', 'cbse-10-math-full-02', 'LA', 5, 0.6, 'The angle of elevation of the top of a building from the foot of the tower is 30 deg and the angle of elevation of the top of the tower from the foot of the building is 60 deg. If the tower is 50 m high, find the height of the building.', 'The angle of elevation of the top of a building from the foot of the tower is $30^\circ$ and the angle of elevation of the top of the tower from the foot of the building is $60^\circ$. If the tower is $50\text{ m}$ high, find the height of the building.', 1, '<svg viewBox=''0 0 200 200''><line x1=''50'' y1=''180'' x2=''150'' y2=''180'' stroke=''black'' stroke-width=''2''/><line x1=''50'' y1=''180'' x2=''50'' y2=''100'' stroke=''black'' stroke-width=''2''/><line x1=''150'' y1=''180'' x2=''150'' y2=''20'' stroke=''black'' stroke-width=''2''/><line x1=''50'' y1=''180'' x2=''150'' y2=''20'' stroke=''black'' stroke-dasharray=''4''/><line x1=''150'' y1=''180'' x2=''50'' y2=''100'' stroke=''black'' stroke-dasharray=''4''/><text x=''155'' y=''100''>Tower (50m)</text><text x=''10'' y=''140''>Building</text></svg>', NULL, '{"steps":["Let height of building = h. Height of tower = 50 m.","Let distance between them = x.","In triangle (Tower, foot of building): tan 60 = 50/x","√3 = 50/x => x = 50/√3","In triangle (Building, foot of tower): tan 30 = h/x","1/√3 = h/x => h = x/√3","Substitute x: h = (50/√3) / √3 = 50/3","h = 16.67 m"],"steps_latex":["Let height of building $= h$, distance between feet $= x$.","In $\\Delta$ with Tower ($50\\text{ m}$) and angle $60^\\circ$:","$\\tan 60^\\circ = \\frac{50}{x} \\Rightarrow \\sqrt{3} = \\frac{50}{x} \\Rightarrow x = \\frac{50}{\\sqrt{3}}$","In $\\Delta$ with Building ($h$) and angle $30^\\circ$:","$\\tan 30^\\circ = \\frac{h}{x} \\Rightarrow \\frac{1}{\\sqrt{3}} = \\frac{h}{x} \\Rightarrow h = \\frac{x}{\\sqrt{3}}$","Substitute $x$: $h = \\frac{50/\\sqrt{3}}{\\sqrt{3}} = \\frac{50}{3}$","Height of building $= 16\\frac{2}{3}\\text{ m}$"],"answer":{"correct_answer_plain":"16.67 m","correct_answer_latex":"$16\\frac{2}{3}\\text{ m}$"}}', NULL, NULL, NULL, 35);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-02-U2-B2C-Q02', 'cbse-10-math-full-02', 'LA', 5, 0.7, 'Two water taps together can fill a tank in 9 3/8 hours. The tap of larger diameter takes 10 hours less than the smaller one to fill the tank separately. Find the time in which each tap can separately fill the tank.', 'Two water taps together can fill a tank in $9\frac{3}{8}$ hours. The tap of larger diameter takes $10\text{ hours}$ less than the smaller one to fill the tank separately. Find the time in which each tap can separately fill the tank.', 0, NULL, NULL, '{"steps":["9 3/8 hours = 75/8 hours.","Let smaller tap take x hours. Larger tap takes x-10 hours.","Part filled in 1 hr: 1/x + 1/(x-10) = 8/75","Simplify: (x-10+x)/(x(x-10)) = 8/75","75(2x-10) = 8(x²-10x)","150x - 750 = 8x² - 80x","8x² - 230x + 750 = 0","4x² - 115x + 375 = 0","Quadratic Formula: x = (115 ± √(115² - 4*4*375))/8","x = (115 ± √(13225 - 6000))/8 = (115 ± √7225)/8 = (115 ± 85)/8","x = 200/8 = 25 or x = 30/8 = 3.75","If x=3.75, larger tap time negative (3.75-10). Reject.","So x=25. Larger tap = 15."],"steps_latex":["Total time $= 9\\frac{3}{8} = \\frac{75}{8}\\text{ hours}$.","Let smaller tap take $x\\text{ hrs}$, larger tap takes $(x-10)\\text{ hrs}$.","$\\frac{1}{x} + \\frac{1}{x-10} = \\frac{8}{75}$","$\\frac{2x-10}{x^2-10x} = \\frac{8}{75}$","$75(2x-10) = 8(x^2-10x)$","$150x - 750 = 8x^2 - 80x$","$8x^2 - 230x + 750 = 0 \\Rightarrow 4x^2 - 115x + 375 = 0$","$x = \\frac{115 \\pm \\sqrt{13225 - 6000}}{8} = \\frac{115 \\pm 85}{8}$","$x = \\frac{200}{8} = 25$ or $x = \\frac{30}{8} = 3.75$","If $x=3.75, x-10$ is negative. Reject.","Smaller tap $= 25\\text{ hrs}$, Larger tap $= 15\\text{ hrs}$."],"answer":{"correct_answer_plain":"Smaller: 25h, Larger: 15h","correct_answer_latex":"\\text{Smaller: } 25\\text{ h, Larger: } 15\\text{ h}"}}', NULL, NULL, NULL, 36);
