INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U1-S3-Q01', 'cbse-10-math-full-03', 'SA', 2, 0.4, 'Explain why 7 × 11 × 13 + 13 is a composite number.', 'Explain why $7 \times 11 \times 13 + 13$ is a composite number.', 0, NULL, NULL, '{"steps_latex":["Expression $= 13(7 \\times 11 + 1)$","$= 13(77 + 1) = 13 \\times 78$","Since the number has factors other than $1$ and itself (13 and 78), it is **composite**."],"answer":{"correct_answer_latex":"\\text{Composite}"}}', NULL, NULL, NULL, 22);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U1-S3-Q02', 'cbse-10-math-full-03', 'SA', 3, 0.5, 'Prove that 2 - 3√5 is an irrational number, given that √5 is irrational.', 'Prove that $2 - 3\sqrt{5}$ is an irrational number, given that $\sqrt{5}$ is irrational.', 0, NULL, NULL, '{"steps_latex":["Let $2 - 3\\sqrt{5} = r$ (rational).","$3\\sqrt{5} = 2 - r$","$\\sqrt{5} = \\frac{2 - r}{3}$","LHS ($\\sqrt{5}$) is irrational. RHS is rational (operation on rationals).","Contradiction. Hence $2 - 3\\sqrt{5}$ is irrational."]}', NULL, NULL, NULL, 23);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U3-S3-Q05', 'cbse-10-math-full-03', 'SA', 3, 0.5, 'Find the point on the x-axis which is equidistant from (2, -5) and (-2, 9).', 'Find the point on the $x$-axis which is equidistant from $(2, -5)$ and $(-2, 9)$.', 0, NULL, NULL, '{"steps_latex":["Let point on x-axis be $P(x, 0)$.","$PA^2 = PB^2$","$(x-2)^2 + (0 - (-5))^2 = (x - (-2))^2 + (0 - 9)^2$","$x^2 - 4x + 4 + 25 = x^2 + 4x + 4 + 81$","$-4x + 29 = 4x + 85$","$-8x = 56 \\Rightarrow x = -7$","Point is $(-7, 0)$."],"answer":{"correct_answer_latex":"$(-7, 0)$"}}', NULL, NULL, NULL, 24);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U5-S3-Q08', 'cbse-10-math-full-03', 'SA', 3, 0.6, 'If tan (A + B) = sqrt(3) and tan (A - B) = 1/sqrt(3), find A and B.', 'If $\tan(A + B) = \sqrt{3}$ and $\tan(A - B) = \frac{1}{\sqrt{3}}$, $0^\circ < A+B \le 90^\circ$, $A > B$, find $A$ and $B$.', 0, NULL, NULL, '{"steps_latex":["$\\tan(A+B) = \\tan 60^\\circ \\Rightarrow A+B = 60^\\circ$ ... (i)","$\\tan(A-B) = \\tan 30^\\circ \\Rightarrow A-B = 30^\\circ$ ... (ii)","Add (i) and (ii): $2A = 90^\\circ \\Rightarrow A = 45^\\circ$","Subtract: $2B = 30^\\circ \\Rightarrow B = 15^\\circ$"],"answer":{"correct_answer_latex":"$A=45^\\circ, B=15^\\circ$"}}', NULL, NULL, NULL, 25);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U7-S3-Q10', 'cbse-10-math-full-03', 'SA', 3, 0.5, 'A die is thrown once. Find the probability of getting (i) a prime number (ii) a number lying between 2 and 6.', 'A die is thrown once. Find the probability of getting:
(i) a prime number
(ii) a number lying between $2$ and $6$.', 0, NULL, NULL, '{"steps_latex":["Total outcomes $= 6$ {1,2,3,4,5,6}","(i) Primes: {2, 3, 5} (3 numbers). $P(\\text{Prime}) = \\frac{3}{6} = \\frac{1}{2}$","(ii) Between 2 and 6: {3, 4, 5} (3 numbers). $P(\\text{Between}) = \\frac{3}{6} = \\frac{1}{2}$"],"answer":{"correct_answer_latex":"$(i) \\frac{1}{2}, (ii) \\frac{1}{2}$"}}', NULL, NULL, NULL, 26);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U6-S3-CS1', 'cbse-10-math-full-03', 'CaseStudy', 4, 0.5, 'An ice cream cone has a height of $12\text{ cm}$ and radius $6\text{ cm}$. A scoop of ice cream (hemisphere) is placed on top.', 'An ice cream cone has a height of $12\text{ cm}$ and radius $6\text{ cm}$. A scoop of ice cream (hemisphere) is placed on top.', 1, '<svg viewBox=''0 0 100 200''><path d=''M 50 150 L 20 50 L 80 50 Z'' fill=''#ffeebb'' stroke=''brown''/><path d=''M 20 50 A 30 30 0 0 1 80 50'' fill=''pink'' stroke=''red''/></svg>', NULL, '{}', NULL, NULL, '{"has_diagram":false,"visual_diagram":null,"sub_questions":[{"part":"i","marks":1,"question":{"text_latex":"Volume of conical part."},"solution":{"steps_latex":["$\\frac{1}{3} \\pi (6^2)(12) = 144\\pi$"]}},{"part":"ii","marks":1,"question":{"text_latex":"Volume of hemispherical part."},"solution":{"steps_latex":["$\\frac{2}{3} \\pi (6^3) = 144\\pi$"]}},{"part":"iii","marks":2,"question":{"text_latex":"Total Volume."},"solution":{"steps_latex":["$144\\pi + 144\\pi = 288\\pi \\approx 904.32$"]}}]}', 27);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U2-S3-Q03', 'cbse-10-math-full-03', 'LA', 5, 0.7, 'A motor boat whose speed is 18 km/h in still water takes 1 hour more to go 24 km upstream than to return downstream to the same spot. Find the speed of the stream.', 'A motor boat whose speed is $18\text{ km/h}$ in still water takes $1\text{ hour}$ more to go $24\text{ km}$ upstream than to return downstream to the same spot. Find the speed of the stream.', 0, NULL, NULL, '{"steps_latex":["Let speed of stream $= x$ km/h.","Speed upstream $= 18-x$. Speed downstream $= 18+x$.","Time upstream - Time downstream = 1","$\\frac{24}{18-x} - \\frac{24}{18+x} = 1$","$24[\\frac{(18+x) - (18-x)}{324 - x^2}] = 1$","$24(2x) = 324 - x^2$","$x^2 + 48x - 324 = 0$","Roots (using quad formula): $x = 6, x = -54$.","Speed cannot be negative. Thus $x = 6\\text{ km/h}$."],"answer":{"correct_answer_latex":"$6\\text{ km/h}$"}}', NULL, NULL, NULL, 28);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-03-U4-S3-Q07', 'cbse-10-math-full-03', 'LA', 5, 0.6, 'Prove that the lengths of tangents drawn from an external point to a circle are equal. Using this, prove that a parallelogram circumscribing a circle is a rhombus.', 'Prove that the lengths of tangents drawn from an external point to a circle are equal. Using this, prove that a parallelogram circumscribing a circle is a **rhombus**.', 1, '<svg viewBox=''0 0 200 200''><circle cx=''100'' cy=''100'' r=''50'' stroke=''black'' fill=''none''/><rect x=''40'' y=''40'' width=''120'' height=''120'' stroke=''black'' fill=''none''/></svg>', NULL, '{"steps_latex":["**Part 1:** Standard Proof of Tangent Lengths (RHS Congruence).","**Part 2:** Let parallelogram be $ABCD$.","$AB = CD, AD = BC$ (Opp sides equal).","Tangents: $AP=AS, BP=BQ, CR=CQ, DR=DS$.","$AB + CD = (AP+BP) + (CR+DR) = AS+BQ+CQ+DS = (AS+DS) + (BQ+CQ) = AD + BC$.","$2AB = 2AD \\Rightarrow AB = AD$.","Since adjacent sides are equal, it is a **Rhombus**."]}', NULL, NULL, NULL, 29);
