INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-S4-Fill-Gen12', 'cbse-10-math-full-04', 'SA', 2, 0.5, 'Find the discriminant of $3x^2 - 2x + INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-U1-S4-Q01', 'cbse-10-math-full-04', 'SA', 2, 0.4, 'Find the HCF and LCM of 6, 72 and 120, using the prime factorization method.', 'Find the HCF and LCM of $6, 72$ and $120$, using the prime factorization method.', 0, NULL, NULL, '{"steps_latex":["$6 = 2 \\times 3$","$72 = 2^3 \\times 3^2$","$120 = 2^3 \\times 3 \\times 5$","HCF $= 2^1 \\times 3^1 = 6$","LCM $= 2^3 \\times 3^2 \\times 5^1 = 8 \\times 9 \\times 5 = 360$"],"answer":{"correct_answer_latex":"HCF$=6$, LCM$=360$"}}', NULL, NULL, NULL, 23);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-U4-S4-Q05', 'cbse-10-math-full-04', 'SA', 3, 0.5, 'Two concentric circles are of radii 5 cm and 3 cm. Find the length of the chord of the larger circle which touches the smaller circle.', 'Two concentric circles are of radii $5\text{ cm}$ and $3\text{ cm}$. Find the length of the chord of the larger circle which touches the smaller circle.', 1, '<svg viewBox=''0 0 200 200''><circle cx=''100'' cy=''100'' r=''80'' stroke=''black'' fill=''none''/><circle cx=''100'' cy=''100'' r=''48'' stroke=''black'' fill=''none''/><line x1=''36'' y1=''100'' x2=''164'' y2=''100'' stroke=''black''/><line x1=''100'' y1=''100'' x2=''100'' y2=''52'' stroke=''black'' stroke-dasharray=''4''/><text x=''110'' y=''80''>3</text><text x=''70'' y=''90''>5</text></svg>', NULL, '{"steps_latex":["Let chord be $AB$ touching smaller circle at $P$. $OP \\perp AB$.","In $\\triangle OPA$: $OA=5, OP=3$.","$AP = \\sqrt{5^2 - 3^2} = \\sqrt{16} = 4$.","Length of chord $AB = 2 \\times AP = 8\\text{ cm}$."],"answer":{"correct_answer_latex":"$8\\text{ cm}$"}}', NULL, NULL, NULL, 24);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-U5-S4-Q06', 'cbse-10-math-full-04', 'SA', 3, 0.6, 'Prove that: (sin A - 2 sin^3 A) / (2 cos^3 A - cos A) = tan A', 'Prove that: $\frac{\sin \theta - 2 \sin^3 \theta}{2 \cos^3 \theta - \cos \theta} = \tan \theta$', 0, NULL, NULL, '{"steps_latex":["LHS $= \\frac{\\sin \\theta(1 - 2\\sin^2 \\theta)}{\\cos \\theta(2\\cos^2 \\theta - 1)}$","Use $\\sin^2 \\theta = 1 - \\cos^2 \\theta$","Numerator term: $1 - 2(1 - \\cos^2 \\theta) = 1 - 2 + 2\\cos^2 \\theta = 2\\cos^2 \\theta - 1$","LHS $= \\frac{\\sin \\theta(2\\cos^2 \\theta - 1)}{\\cos \\theta(2\\cos^2 \\theta - 1)}$","$= \\frac{\\sin \\theta}{\\cos \\theta} = \\tan \\theta = \\text{RHS}$"],"answer":{"correct_answer_latex":"\\text{Proved}"}}', NULL, NULL, NULL, 25);
