INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-U2-S4-Q03', 'cbse-10-math-full-04', 'SA', 3, 0.5, 'If one root of the quadratic equation 4x^2 - 2x + (k-4) = 0 is the reciprocal of the other, find the value of k.', 'If one root of the quadratic equation $4x^2 - 2x + (k-4) = 0$ is the reciprocal of the other, find the value of $k$.', 0, NULL, NULL, '{"steps_latex":["Let roots be $\\alpha$ and $1/\\alpha$.","Product of roots $\\alpha \\cdot (1/\\alpha) = 1$.","Product $= c/a = \\frac{k-4}{4}$.","$\\frac{k-4}{4} = 1 \\Rightarrow k-4 = 4 \\Rightarrow k = 8$."],"answer":{"correct_answer_latex":"$k=8$"}}', NULL, NULL, NULL, 27);
INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-U6-S4-Q07', 'cbse-10-math-full-04', 'LA', 5, 0.6, 'A well of diameter 3m is dug 14 m deep. The earth taken out of it has been spread evenly all around it in the shape of a circular ring of width 4m to form an embankment. Find the height of the embankment.', 'A well of diameter $3\text{ m}$ is dug $14\text{ m}$ deep. The earth taken out of it has been spread evenly all around it in the shape of a circular ring of width $4\text{ m}$ to form an embankment. Find the height of the embankment.', 0, NULL, NULL, '{"steps_latex":["Radius of well $r = 1.5\\text{ m}$. Depth $h = 14\\text{ m}$.","Volume of earth $= \\pi r^2 h = \\pi (1.5)^2 (14) = 31.5\\pi$.","Embankment is a hollow cylinder. Inner radius $r_1 = 1.5$. Width $= 4$.","Outer radius $r_2 = 1.5 + 4 = 5.5\\text{ m}$.","Area of base $= \\pi (r_2^2 - r_1^2) = \\pi (5.5^2 - 1.5^2) = \\pi (30.25 - 2.25) = 28\\pi$.","Height $H = \\text{Volume} / \\text{Area} = \\frac{31.5\\pi}{28\\pi} = 1.125\\text{ m}$."],"answer":{"correct_answer_latex":"$1.125\\text{ m}$"}}', NULL, NULL, NULL, 28);
