INSERT INTO questions (id, set_id, type, marks, difficulty, question_text, question_latex, has_diagram, diagram_svg, options_json, solution_json, unit, topic, case_study_json, sort_order) VALUES ('cbse-10-math-full-04-U2-S4-Q02', 'cbse-10-math-full-04', 'LA', 5, 0.6, 'The sum of a two-digit number and the number obtained by reversing the digits is 66. If the digits of the number differ by 2, find the number. How many such numbers are there?', 'The sum of a two-digit number and the number obtained by reversing the digits is $66$. If the digits of the number differ by $2$, find the number. How many such numbers are there?', 0, NULL, NULL, '{"steps_latex":["Let digits be $x$ and $y$. Number $= 10x + y$. Reversed $= 10y + x$.","$(10x+y) + (10y+x) = 66 \\Rightarrow 11(x+y) = 66 \\Rightarrow x+y = 6$.","Given difference is 2: $|x-y| = 2$.","Case 1: $x-y=2$. Add to $x+y=6 \\Rightarrow 2x=8 \\Rightarrow x=4, y=2$. Number $42$.","Case 2: $y-x=2$. Sub from $x+y=6 \\Rightarrow 2y=8 \\Rightarrow y=4, x=2$. Number $24$.","There are **two** such numbers: $42$ and $24$."],"answer":{"correct_answer_latex":"$42$ and $24$"}}', NULL, NULL, NULL, 33);
